import delay from './delay'

const arrSort = async (arr,key=null,type=null) => {
   console.log('arrSortDelay')
   await delay(1000)
   if (type === 'num') {
      return arr.sort((a,b) => a[key] - b[key])
   }else {
      return arr
   }
}

export default arrSort
