import React from 'react'

import { MainLayout } from '../../Layout/MainLayout'
import JobOpeningsComponent from '../../components/JobOpeningsComponent/JobOpeningsComponent'


const JobOpenings = ({vacations : serverVacations}) => {

  return (
    <MainLayout title={'Вакансии'}>
      <div className={'flexWrapper'}>
        {
          <JobOpeningsComponent
              vacations={serverVacations}/>
        }
      </div>
    </MainLayout>

  )
}

export const getServerSideProps = async () => {
  const response = await fetch('http://wp-test/wp-json/custom-routes/v1/newjobfiltered')
  const responseData = await response.json()
  const vacations = []
  responseData.forEach(postData => {
    const vacation = {
      id: postData.id,
      title: postData.title,
      excerpt: postData.excerpt,
      propierties:[]
    }
    if(postData.propiertiesTitle1){
      vacation.propierties.push({
        propiertie: postData.propierties1,
        title: postData.propiertiesTitle1
      })
    }
    if(postData.propiertiesTitle2){
      vacation.propierties.push({
        propiertie: postData.propierties2,
        title: postData.propiertiesTitle2
      })
    }
    if(postData.propiertiesTitle3){
      vacation.propierties.push({
        propiertie: postData.propierties3,
        title: postData.propiertiesTitle3
      })
    }
    vacations.push(vacation)
  })
  return {
    props: {vacations}
  }
}

export default JobOpenings
