import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { MainLayout } from '../../Layout/MainLayout'
import JobOpeningsComponent from '../../components/JobOpeningsComponent/JobOpeningsComponent'
import { fetchVacations } from '../../Store/actions/vacationAction'
import Loader from '../../components/Loader/Loader'

const JobOpenings = ({vacations : serverVacations} = null) => {
  const dispatch = useDispatch()
  const loading = useSelector(state => state.vacations.loading)
  const vacationsRedux = useSelector(state => state.vacations.vacations)


  useEffect(() =>{
    if(serverVacations) {
      return
    }
    if(vacationsRedux){
      return
    }
    dispatch(fetchVacations())
  },[])



  return (
    <MainLayout title={'Вакансии'}>
     {/* <div className={'flexWrapper'}>
        {
          loading || !serverVacations && !vacationsRedux
            ? <Loader/>
            : <JobOpeningsComponent
                vacations={serverVacations || vacationsRedux}
              />
        }
      </div>*/}
    </MainLayout>

  )
}

export default JobOpenings

export const getServerSideProps = async () => {
  const response = await fetch('http://wp-test//wp-json/wp/v2/jobopenings')
  const responseData = await response.json()
  const vacations = []
  responseData.forEach(postData => {
    const vacation = {
      id: postData.id,
      date: postData.date,
      title: postData.title.rendered,
      type: postData.type,
      author: postData.author,
      excerpt: postData.excerpt.rendered,
      content: postData.content.rendered
    }
    vacations.push(vacation)
  })
  console.log(responseData)
  return {
    props: {vacations}
  }
}
