import {useState} from 'react'
import classes from './saleComponent.module.scss'

const SaleComponent = ({motors}) => {

  const [table, setTable] = useState(motors)
  const [sortTable, setSortTable] = useState(motors)
  const [sortState, setSortState] = useState({
    num: 1,
    power: 2,
    capacity: 3,
    type: 4,
    speed: 5,
    price: 6
  })
  /*pagination*/
  const [pagPage, setPagPage]  = useState(1)
  const [pagValue, setPagValue] = useState(25)
  const [pagPages, setPagPages] = useState(Math.ceil(motors.length/pagValue))
  const pagNum = () => {
    const arr =[]
    for(let i = 1; i<= pagPages; i++){
      let btnClass = ''
      if (i === pagPage){
        btnClass = 'btn-pagination-active'
      }else {
        btnClass = 'btn-pagination'
      }

      arr.push(
          <button key={i} className={btnClass}>{i}</button>
          )
    }
    return arr
  }
  const paginationHandler = (type) => {
    if(type==='-'){
      if(pagPage>=2){
        setPagPage(pagPage - 1)
      }else setPagPage(1)
    }else if (type === '+'){
      if(pagPage <= pagPages-1){
        setPagPage(pagPage +1)
      }else setPagPage(pagPages)
    }
  }
  /*end pagination */

  const sortHandler = (e) => {
    const value = e.target.value
    console.log(value)
    switch (value) {
      case 1: {
        sortTable.sort((a,b) => {
          return a[0].substring(3,6) - b[0].substring(3,6)
        })
        setSortState({...sortState, num: -1})
        setTable(sortTable)
        break
      }
      case -1: {
        sortTable.sort((a,b) => {
          return b[0].substring(3,6) - a[0].substring(3,6)
        })
        setSortState({...sortState, num: 1})
        setTable(sortTable)
        break
      }
      case 2: {
        sortTable.sort((a,b) => {
          return a[5] - b[5]
        })
        setSortState({...sortState, power: -2})
        setTable(sortTable)
        break
      }
      case -2: {
        sortTable.sort((a,b) => {
          return b[5] - a[5]
        })
        setSortState({...sortState, power: 2})
        setTable(sortTable)
        break
      }
      case 3: {
        sortTable.sort((a,b) => {
          console.log(a[3].trim())
          return a[3].trim() > b[3].trim()
        })
        setSortState({...sortState, capacity: -3})
        setTable(sortTable)
        break
      }
      case -3: {
        sortTable.sort((a,b) => {
          return b[3] - a[3]
        })
        setSortState({...sortState, capacity: 3})
        setTable(sortTable)
        break
      }
      case 4: {
        sortTable.sort((a,b) => {

          return a[3] > b[3]
        })
        setSortState({...sortState, type: -4})
        setTable(sortTable)
        break
      }
      case -4: {
        sortTable.sort((a,b) => {
          return b[3] < a[3]
        })
        setSortState({...sortState, type: 4})
        setTable(sortTable)
        break
      }
      case 6: {
        sortTable.sort((a, b) => {
          return a[8].split(',',2).join('')-b[8].split(',',2).join('')
        })
        setSortState({...sortState, price: -6})
        setTable(sortTable)
        break
      }
      case -6: {
        sortTable.sort((a, b) => {
          return  - a[8].split(',',2).join('') + b[8].split(',',2).join('')
        })
        setSortState({...sortState, price: 6})
        setTable(sortTable)
        break
      }
    }
  }
  return (
      <div className={classes.saleComponent}>
        <div className="container">
          <h2>Электрооборудование в наличии</h2>
          <h4>Цены приведены без учета НДС на 11.07.18 г.</h4>
          <div className={classes.table}>
            <ul>
              <li value={sortState.num} onClick={sortHandler}>Номер</li>
              <li value={sortState.power} onClick={sortHandler}>кВт</li>
              <li value={sortState.capacity} onClick={sortHandler}>Комплектность</li>
              <li value={sortState.type} onClick={sortHandler}>Обозначение/Тип</li>
              <li value={sortState.speed} onClick={sortHandler}>об/мин</li>
              <li value={sortState.price} onClick={sortHandler}>Цена,Р</li>
            </ul>
            {
              table.map((item, index) =>{
                if(index===0){
                  return null
                }
                if(index>(pagPage-1)*pagValue && index<=(pagPage)*pagValue){
                  const price = item[8].split(',',2).join('')
                  return(
                      <ul key={index}>
                        <li>{item[0]}</li>
                        <li>{item[5]}</li>
                        <li>{item[3]}</li>
                        <li>{item[4]}</li>
                        <li>{item[6]}</li>
                        <li>{price}</li>
                      </ul>)
                }

                  }
              )
            }
          </div>
          <div className={classes.pagination}>
            <div onClick={() => paginationHandler('-')} className="btn">
              prev
            </div>
            <div className={classes.paginationWrapper}>
              {pagNum()}
            </div>
            <div onClick={() => paginationHandler('+')} className="btn">
              next
            </div>
          </div>

        </div>
      </div>
  )
}

export default SaleComponent
