import {useState} from 'react'
import classes from './saleComponent.module.scss'

const SaleComponent = ({motors}) => {

  const [pagPage, setPagPage]  = useState(1)
  const [pagValue, setPagValue] = useState(20)
  const [pagPages, setPagPages] = useState(Math.ceil(motors.length/pagValue))
  const pagNum = () => {
    const arr =[]
    let btnClass = 'btn-pagination'
    for(let i = 1; i<= pagPages; i++){
      if (i===pagPage){
        btnClass = 'btn'
      }
      arr.push(
          <button className={btnClass}>{i}</button>
          )
    }
    return arr
  }
  return (
      <div className={classes.saleComponent}>
        <div className="container">
          <h2>Электрооборудование в наличии</h2>
          <h4>Цены приведены без учета НДС на 11.07.18 г.</h4>
          <div className={classes.table}>
            <ul>
              <li>Номер</li>
              <li>кВт</li>
              <li>Комплектность</li>
              <li>Обозначение/Тип</li>
              <li>об/мин</li>
              <li>Цена,Р</li>
            </ul>
            {
              motors.map((item, index) =>{
                if(index===0){
                  return null
                }
                const price = item[8].split(',',2).join('')
                return(
                <ul key={index}>
                  <li>{item[0]}</li>
                  <li>{item[5]}</li>
                  <li>{item[3]}</li>
                  <li>{item[4]}</li>
                  <li>{item[6]}</li>
                  <li>{price}</li>
                </ul>)
                  }
              )
            }
          </div>
          <div className={classes.pagination}>
            <div className="btn">
              prev
            </div>
            {pagNum()}
            <div className="btn">
              next
            </div>

          </div>
        </div>
      </div>
  )
}

export default SaleComponent
