import { useRouter } from 'next/router'
import { MainLayout } from '../../Layout/MainLayout'
import classes from './JobOpening.module.scss'
import parse from 'html-react-parser'

export default function oneVacation({vacation: serverVacation}) {
  const router = useRouter()
  const linkClickHandler = () => {
    router.push('/JobOpenings')
  }
  console.log(parse(serverVacation.content))
  return (
    <MainLayout>
      <div className={'flexWrapper'}>
        <div className={classes.jobOpening}>
          <div className="container">
            <h2>{serverVacation.title}</h2>
            <div className={classes.oneJob}>
              { parse(serverVacation.content) }
            </div>
            <button onClick={linkClickHandler}>Back</button>
          </div>
        </div>
      </div>
    </MainLayout>
  )
}

export const getServerSideProps = async (ctx) => {
  const id = ctx.query.id
  const response = await fetch(`http://wp-test//wp-json/wp/v2/jobopenings/${id}`)
  const responseData = await response.json()
  const vacation = {
    id: responseData.id,
    date: responseData.date,
    title: responseData.title.rendered,
    content: responseData.content.rendered,
  }
  return {
    props: {vacation}
  }
}
