import { useRouter } from 'next/router'
import { MainLayout } from '../../Layout/MainLayout'

export default function oneVacation({vacation: serverVacation}) {
  const router = useRouter()
  const linkClickHandler = () => {
    router.push('/JobOpenings')
  }
  console.log(serverVacation)
  return (
    <MainLayout>
      <h2>Vacation {router.query.id}</h2>
      <button onClick={linkClickHandler}>Back</button>
    </MainLayout>
  )
}

export const getServerSideProps = async () => {
  const id = useRouter().query.id
  const response = await fetch(`http://wp-test//wp-json/wp/v2/jobopenings/${id}`)
  const responseData = await response.json()
  const vacation = {}
  responseData.forEach(postData => {
    const vacation = {
      id: postData.id,
      date: postData.date,
      title: postData.title.rendered,
      content: postData.content.rendered,
    }
  })
  return {
    props: vacation
  }
}
