import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { MainLayout } from '../../Layout/MainLayout'
import JobOpeningsComponent from '../../components/JobOpeningsComponent/JobOpeningsComponent'
import { fetchVacations } from '../../Store/actions/vacationAction'
import Loader from '../../components/Loader/Loader'
import parse from 'html-react-parser'

const JobOpenings = ({vacations : serverVacations}) => {
  const [vacations, setVacations] = useState(serverVacations)
  const dispatch = useDispatch()
  const loading = useSelector(state => state.vacations.loading)
  // const vacations = useSelector(state => state.vacations.vacations)
  useEffect(() =>{
    /*if(Object.keys(vacations).length){
      return
    }*/
    dispatch(fetchVacations())
    setVacations(useSelector(state => state.vacations.vacations))
  },[])

  return (
    <MainLayout title={'Вакансии'}>
      <div className={'flexWrapper'}>
        {
          vacations
            ? <Loader/>
            : <JobOpeningsComponent
                vacations={vacations}
              />
        }
      </div>
    </MainLayout>

  )
}

export default JobOpenings
JobOpenings.getInitialProps = async ({ req }) => {
  if (!req) {
    return {
      vacations: null
    }
  }
  const response = await fetch('http://wp-test//wp-json/wp/v2/jobopenings')
  const responseData = await response.json()
  const vacations = []
  responseData.forEach(postData => {
    const content = parse(postData.content.rendered)
    const vacation = {
      id: postData.id,
      date: postData.date,
      title: postData.title.rendered,
      type: postData.type,
      author: postData.author,
      content
    }
    vacations.push(vacation)
  })
  return {
    vacations
  }
}
