import React from 'react'
import { useSelector } from 'react-redux'
import classes from './JobOpeningsComponent.module.scss'
import Loader from '../Loader/Loader'

const JobOpeningsComponent = () => {
  const loading = useSelector(state => state.posts.loading)
  const posts = useSelector(state => state.posts.posts)
  // const vacations = posts.filter(post => post.categories === 16)
  return (
      <div className={classes.JobOpeningsComponent}>
        <div className="container">
          {!loading
              ? posts.map(item =>
                  <div key={item.id}>
                    <br/>
                    <hr/>
                    <h2>Заголовок: {item.title}</h2>
                    {item.content}
                    <p>{item.media.map(media => {
                          const url = media.media_details.sizes.medium.source_url
                          return <img key={media.id} src={url} alt=""/>
                        }
                    )}</p>
                    <i>{item.date}</i>
                    <hr/>
                    <br/>
                  </div>
              )
              : <Loader/>}
        </div>
      </div>
  )
}

export default JobOpeningsComponent
