import { useRouter } from 'next/router'
import { MainLayout } from '../../Layout/MainLayout'
import classes from './JobOpening.module.scss'
import parse from 'html-react-parser'

export default function oneVacation({vacation: serverVacation}) {
  const router = useRouter()
  const linkClickHandler = () => {
    router.push('/JobOpenings')
  }
  //console.log(serverVacation)
  return (
    <MainLayout>
      <div className={'flexWrapper'}>
        <div className={classes.jobOpening}>
          <div className="container">
            {/*<h2>{serverVacation.title}</h2>
            <div className={classes.oneJob}>
              { parse(serverVacation.content) }
            </div>
            <button onClick={linkClickHandler}>Back</button>*/}
          </div>
        </div>
      </div>
    </MainLayout>
  )
}

export const getServerSideProps = async (ctx) => {
  const id = ctx.query.id
  const response = await fetch(`http://wp-test/wp-json/custom-routes/v1/newjobfilteredid/${id}`)
  const postData = await response.json()

  const vacation = {
    id: postData[0].id,
    title: postData[0].title,
    propierties:[]
  }
  console.log(postData[0].propiertiesTitle1)
  if(postData[0].propiertiesTitle1){
    vacation.propierties.push({
      propiertie: postData.propierties1.toString(),
      title: postData.propiertiesTitle1.toString()
    })
  }
  if(postData[0].propiertiesTitle2){
    vacation.propierties.push({
      propiertie: postData.propierties2.toString(),
      title: postData.propiertiesTitle2.toString()
    })
  }
  if(postData[0].propiertiesTitle3){
    vacation.propierties.push({
      propiertie: postData.propierties3.toString(),
      title: postData.propiertiesTitle3.toString()
    })
  }
  return {
    props: {vacation}
  }
}
