import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { MainLayout } from '../../Layout/MainLayout'
import JobOpeningsComponent from '../../components/JobOpeningsComponent/JobOpeningsComponent'
import { fetchVacations } from '../../Store/actions/vacationAction'
import Loader from '../../components/Loader/Loader'

const JobOpenings = () => {
  const dispatch = useDispatch()
  const loading = useSelector(state => state.vacations.loading)
  const vacations = useSelector(state => state.vacations.vacations)
  useEffect(() =>{
    dispatch(fetchVacations())
  },[])


  return (
    <MainLayout title={'Вакансии'}>
      <div className={'flexWrapper'}>
        {
          loading
            ? <Loader/>
            : <JobOpeningsComponent
                vacations={vacations}
              />
        }
      </div>
    </MainLayout>

  )
}

export default JobOpenings
